type t = {
  legacy_script_hash : Script_expr_hash.t;
  patched_code : Michelson_v1_primitives.prim Micheline.canonical;
  addresses : string list;
}

type patched_script = t

let script_hash : patched_script -> Script_expr_hash.t =
 fun {legacy_script_hash; _} -> legacy_script_hash

let code : patched_script -> Michelson_v1_primitives.prim Micheline.canonical =
 fun {patched_code; _} -> patched_code

let bin_expr_exn hex =
  match
    Option.bind
      (Hex.to_bytes @@ `Hex hex)
      (fun bytes ->
        Data_encoding.Binary.of_bytes_opt Script_repr.expr_encoding bytes)
  with
  | Some expr -> expr
  | None -> raise (Failure "Decoding script failed.")

